function register(n){if(loginFormEl.valid()){var t=userNameEl.val(),i=userNameEl.val(),r={email:t,password:i};$.post("/api/account/Register",r).done(function(n){var t=JSON.parse(n);t.ResultCode<0?showAlert(alertEl,"alert-warning",t.ResultMessage):showAlert(alertEl,"alert-success","Success user registered")}).fail(alertError);n.preventDefault()}}function login(n){if(loginFormEl.valid()){var t=userNameEl.val(),i=userNameEl.val(),r={userName:t,password:i};$.post("/api/account/login",r).done(function(n){var t=JSON.parse(n),r,i,u;if(t.ResultCode<0)showAlert(alertEl,"alert-warning",t.ResultMessage);else{r="";for(i in t)Object.prototype.hasOwnProperty.call(t,i)&&(u=t[i],r+=i+" - "+u+"<br>");showAlert(alertEl,"alert-success",r)}}).fail(alertError);n.preventDefault()}}function alertError(){showAlert(alertEl,"alert-danger","Error")}function clearAlert(n){n.find("#alert-message").html("");n.removeClass("alert-danger");n.removeClass("alert-success")}function showAlert(n,t,i){clearAlert(n);n.find("#alert-message").html(i);n.addClass(t);n.removeClass("d-none");n.addClass("d-block")}function hideAlert(n){clearAlert(n);n.removeClass("d-block");n.addClass("d-none")}var alertEl,userNameEl,passwordEl,loginFormEl;$(document).ready(function(){alertEl=$(".alert");userNameEl=$("#user-name");passwordEl=$("#password");loginFormEl=$("#login-form");loginFormEl.validate();$("#close-alert").click(function(){hideAlert(alertEl)});$("#login-button").click(login);$("#register-button").click(register)});